import * as Tone from 'tone'
import React, { Component } from 'react'

import * as melodySettings from './tunes/melody.js'
import { secondSynthSettings, secondSequenceSettings} from './tunes/bass.js'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let melodySynth
let melodytremolo
let freeverb
let secondSynth
let melodydistortion
let melodychorus
let melodyautoWah
let melodyautoPanner
let melodypingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)

    const freeverbSettings = {
      wet: 1,
      roomSize: 0,
      dampening: 40
    }

    const pingPongDelaySettings = { wet: 0.8, delayTime: 0.2, maxDelayTime: 1 }

    this.state = {
      melodySettings,
      secondSynthSettings,
      freeverbSettings,
      pingPongDelaySettings,
    }
  }

  handleStart = () => {
    const { melodySettings, secondSynthSettings,  } = this.state

    melodySynth = new Tone.Synth(synthSettings)
    secondSynth = new Tone.Synth(secondSynthSettings)
    melodychorus = new Tone.Chorus(chorusSettings).start()
    freeverb = new Tone.Freeverb(freeverbSettings).toDestination();
    melodyautoPanner = new Tone.AutoPanner(autoPannerSettings).toDestination().start();
    melodytremolo = new Tone.Tremolo(tremoloSettings).toDestination()
    melodydistortion = new Tone.Distortion(distortionSettings).toDestination();
    melodyautoWah = new Tone.AutoWah(autoWahSettings).toDestination()
    melodypingPongDelay = new Tone.PingPongDelay(
      pingPongDelay
    ).toDestination()

    melodySynth.chain( tremolo, autoPanner, distortion, pingPongDelay)
    secondSynth.chain( freeverb,chorus)



    const part = new Tone.Part((time, note) => {
      melodySynth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequenceSettings.steps).start(0)

    const secondPart = new Tone.Part((time, note) => {
      secondSynth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, secondSequenceSettings).start(0)

    part.loopEnd = melodySettings.sequence.duration
    part.loop = true
    secondPart.loopEnd = secondSequenceSettings.duration
    secondPart.loop = true
    Tone.Transport.start()
  }



  handleValueChange = (property, value) => {
    const { synthSettings, secondSynthSettings,freeverbSettings, autoPannerSettings, tremoloSettings, distortionSettings, autoWahSettings, chorusSettings, pingPongDelaySettings } = this.state

    if (property === 'synthType') {
      synth.oscillator.type = value
      melodySettings.oscillator.type = value
    } else if (property === 'tremoloDepth') {
      tremolo.depth.value = value
      melodySettings.tremolo.depth = value
    } else if (property === 'tremoloSpread') {
      tremolo.spread = value
      melodySettings.tremolo.spread = value
    } else if (property === 'distortionWet') {
      distortion.wet.value = value
      melodySettings.distortion.wet = value
    } else if (property === 'distortionDist') {
      distortion.distortion.value = value
      melodySettings.distortion.distortion = value
    } else if (property === 'autoPannerWet') {
      autoPanner.wet.value = value
      melodySettings.autoPanner.wet = value
    } else if (property === 'autoPannerFrequency') {
      autoPanner.frequency.value = value
      melodySettings.autoPanner.frequency = value
    } else if (property === 'secondSynthType') {
      secondSynth.oscillator.type = value
      secondSynthSettings.oscillator.type = value
    } else if (property === 'freeverbRoomSize') {
      freeverb.roomSize.value = value
      freeverbSettings.roomSize = value
    } else if (property === 'freeverbWet') {
      freeverb.wet.value = value
      freeverbSettings.wet = value
    } else if (property === 'pingPongDelayWet') {
      pingPong.wet.value = value
      melodySettings.pingPongDelay.wet = value
    } else if (property === 'pingPongDelayDelayTime') {
      pingPongDelay.delayTime.value = value
      melodySettings.pingPongDelay.delayTime = value
    }


    this.setState({
      secondSynthSettings,
      freeverbSettings,
      synthSettings,
      melodySettings,
      tremoloSettings,
      chorusSettings,
      autoWahSettings,
      pingPongDelaySettings,
      distortionSettings,
      autoPannerSettings
    })
  }

  render() {
    const { synthSettings, melodySettings, secondSynthSettings, freeverbSettings, autoPannerSettings, distortionSettings, autoWahSettings, tremoloSettings, rythmSettings, chorusSettings, pingPongDelaySettings } = this.state

    const options = ['sine', 'square', 'sawtooth', 'triangle']
    const optionstwo = ['sine', 'square', 'sawtooth', 'triangle']
    const wet = ['0', '1']

    return (
      <div className="Container">
        <SC_Button
          text="Start"
          handleClick={this.handleStart}
        />
      <div className="ContainerOfEffects">
      <div className="First">
        <div className="FirstEffect">
            <SC_ToggleButtonSet
              name="Type"
              options={options}
              value={synthSettings.oscillator.type}
              property="synthType"
              handleChange={this.handleValueChange}
            />

          <div className="Columns">
            <div className="columnOfEffect">
            <SC_Slider
              name="Tremolo Depth"
              min={0}
              max={1}
              step={0.01}
              value={tremoloSettings.depth}
              property="tremoloDepth"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="Tremolo Spread"
              min={1}
              max={200}
              step={1}
              value={tremoloSettings.spread}
              property="tremoloSpread"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="AutoPanner Wet"
              min={0}
              max={100}
              step={0.1}
              value={autoPannerSettings.frequency}
              property="autoPannerFrequency"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="PingPongDelay Wet"
              min={0}
              max={1}
              step={0.1}
              value={pingPongDelaySettings.frequency}
              property="pingPongDelayWet"
              handleChange={this.handleValueChange}
            />

          </div>
          <div className="columnOfEffect">
            <SC_Slider
              name="Distortion Wet"
              min={0}
              max={1}
              step={0.1}
              value={distortionSettings.wet}
              property="distortionWet"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="Distortion dist"
              min={0}
              max={100}
              step={1}
              value={distortionSettings.dist}
              property="distortionDist"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="AutoPanner Wet"
              min={0}
              max={1}
              step={0.01}
              value={autoPannerSettings.wet}
              property="autoPannerWet"
              handleChange={this.handleValueChange}
            />
            <SC_Slider
              name="PingPongDelay DelayTime"
              min={0}
              max={1}
              step={0.01}
              value={pingPongDelaySettings.delayTime}
              property="pingPongDelayDelayTime"
              handleChange={this.handleValueChange}
            />
            </div>
          </div>
        </div>
          <p className='cursive'>first instrument</p>
        </div>
          <div className="Second">
        <div className="SecondEffect">
        <SC_ToggleButtonSet
          name="Bass"
          options={optionstwo}
          value={secondSynthSettings.oscillator.type}
          property="secondSynthType"
          handleChange={this.handleValueChange}
        />
        <div className="Columns">
