import React from 'react'
import ReactDOM from 'react-dom'

let oscillator
let audioCtx
let container
// const prototypeId = 'prototype_2'


function createOscillator() {

  audioCtx = new (window.AudioContext || window.webkitAudioContext)();
  // create Oscillator node
  oscillator = audioCtx.createOscillator();

  oscillator.type = 'square';
  oscillator.frequency.setValueAtTime(440, audioCtx.currentTime); // value in hertz
  oscillator.connect(audioCtx.destination);
  oscillator.start();
}

function changeValue() {
  const slider = document.getElementById('slider')
  oscillator.frequency.setValueAtTime(slider.value, audioCtx.currentTime); // value in hertz
}

function createSlider() {

  const slider = document.createElement('input')
  slider.type = 'range'
  slider.min = 0
  slider.max = 1000
  slider.value = 440
  slider.id = 'slider'
  container.appendChild(slider)

  slider.addEventListener('input', () => {
    changeValue()
  })
}

function changeOscillatorType(type) {
  oscillator.type = text
}

function createButton(text, callback, parameter) {
 const button = document.createElement('div')
 button.innerText = text
 button.classList.add('button')
 container.appendChild(button)

 button.addEventListener('click', () => {
   callback(parameter)
 })
}

function createOscillatorTypeButtons() {
  // добавляем типы кнопок для изменения вида звука
  const types = ['sine', 'square', 'sawtooth', 'triangle']
  // для каждой кнопки выполняется функция цикла
  types.forEach((type, i) => {
        createButton(type, changeOscillatorType)
  })
}

document.addEventListener('DOMContentLoaded', () => {


  const frame = document.createElement('div')
  frame.innerText = 'Art Design & Coding Community'
  frame.classList.add('frame')

  container = document.getElementById('prototype_2')
  container.appendChild(frame)

  frame.addEventListener('click', () => {
    createOscillator()
    createSlider()
    createOscillatorTypeButtons()
  })
})
