import React from 'react'
import ReactDom from 'react-dom'

let oscillator
let audioCtx

const container = document.createElement('div')
const frameLeft = document.createElement('div')
const frameRight = document.createElement('div')
const synthText = document.createElement('div')
const protoText = document.createElement('div')
const windowMain = document.createElement('div')
const frameTop = document.createElement('div')
const frameTopLeft = document.createElement('div')
const frameTopRight = document.createElement('div')
const bottomImages = document.createElement('div')
const rubyText = document.createElement('p')
const reactText = document.createElement('p')

rubyText.innerText = 'ruby 3.0.2p107 (2021-07-07 revision 0db68f0233) [x86_64-darwin20]'
reactText.innerText = 'rails 7.0.3.1'

frameTop.classList.add('frame_top')
frameRight.classList.add('frame_right')
bottomImages.classList.add('bottom_image')
rubyText.classList.add('p')
frameTopRight.classList.add('frame_right')
frameTopLeft.classList.add('frame_left_top')
synthText.classList.add('h1_text')
windowMain.classList.add('window_img')
protoText.classList.add('h3_text')
frameLeft.classList.add('frame_left')

container.appendChild(frameLeft)



function createOscillator() {

  audioCtx = new (window.AudioContext || window.webkitAudioContext)();
  // create Oscillator node
  oscillator = audioCtx.createOscillator();

  oscillator.type = 'square';
  oscillator.frequency.setValueAtTime(50, audioCtx.currentTime); // value in hertz
  oscillator.connect(audioCtx.destination);
  oscillator.start();
}

function createTone() {

  audioCtx = new (window.AudioContext || window.webkitAudioContext)();
  // create Oscillator node
  oscillator = audioCtx.createOscillator();

  oscillator.type = 'square';
  oscillator.frequency.setValueAtTime(100, audioCtx.currentTime); // value in hertz
  oscillator.connect(audioCtx.destination);
  oscillator.start();
}

function changeValue() {
  const slider = document.getElementById('slider')
  oscillator.frequency.setValueAtTime(slider.value, audioCtx.currentTime); // value in hertz
}

function createSlider() {
  const container = document.getElementById('prototype_3')
  const slider = document.createElement('input')
  slider.type = 'range'
  slider.min = 0
  slider.max = 1000
  slider.value = 220
  slider.id = 'slider'

  slider.addEventListener('input', () => {
    changeValue()
  })
}

document.addEventListener('DOMContentLoaded', () => {

  const container = document.getElementById('prototype_3')
  const frame = document.createElement('div')
  const cube = document.createElement('div')
  frame.innerText = 'Art Design & Coding Community'
  frame.classList.add('frame')
  cube.classList.add('cube')
  container.appendChild(frameLeft)
  container.appendChild(frameRight)
  frameLeft.appendChild(synthText)
  frameLeft.appendChild(protoText)
  frameLeft.appendChild(windowMain)
  frameRight.appendChild(frameTop)
  // frameTop.appendChild(frameTopLeft)
  frameTopLeft.appendChild(rubyText)
  frameTopLeft.appendChild(reactText)
  frameTop.appendChild(frameTopRight)


  cube.addEventListener('click', () => {
    createTone()
  })

  createSlider()

  frame.addEventListener('click', () => {
    createOscillator()
  })

  cube.addEventListener('click', () => {
    createTone()
  })

})
