import React, { Component } from 'react'

import * as Tone from 'tone'

import SC_Button from './SC_Button'

export default class Container extends Component {
  constructor(props) {
    super(props)
  }
 // Собственно синтезатор
  handleClick = () => {
    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sine',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
  }

    const autoPannerSettings = {
      wet: 1,
      type: 'sine',
      frequency: 1,
      depth: 1
    }

    const pingPongDelaySettings = {
      wet: 1,
      delayTime: 0.25,
      maxDelayTime: 1
    }

    const pingPong = new Tone.PingPongDelay("4n", 0.2).toDestination();
    const autoPanner = new Tone.AutoPanner(autoPannerSettings).toDestination().start();
    const synth = new Tone.Synth(synthSettings)
    const channelSettings = { volume: 0, pan: 0, mute: false, solo: false }
    const channel = new Tone.Channel(channelSettings).toDestination()

    synth.chain(autoPanner, channel)

    const sequence = [
      {
        time: '0:0:0',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'C4',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'C4',
        duration: '1n',
        velocity: 1
      },
    ]

    // Создаём партию, добавляем в неё секвенцию
    // и включаем проигрывание
    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    // Указываем длительность партии
    part.loopEnd = '1m'

    // Включаем зацикливание
    part.loop = true

    // Включаем звук в браузере
    // sampler.context.resume()

    // Включаем отсчёт времени в Tone.js
    Tone.Transport.start()



  //Запуск синта
  }


  render() {
    return (
      <div className="Container">
        <SC_Button
          text="puppies"
          handleClick={this.handleClick}
        />
      </div>
    )
  }
}
