import React, { Component } from 'react'
import SC_ToggleButtonSet from '../components/SC_ToggleButtonSet'
import SC_Slider from '../components/SC_Slider'

export default class ToneSynth extends Component {
  constructor(props) {
    super(props)
  }

  render() {
    const { settings, handleValueChange } = this.props

    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="SC_Button">
        <div className="synth_settings">
        <div className="column">

        <SC_Slider
          name="Synth Volume"
          min={-70}
          max={30}
          step={0.1}
          value={settings.synth.volume}
          property="synthVolume"
          handleChange={handleValueChange}
        />

        <SC_Slider
          name="Synth Envelope Attack"
          min={0}
          max={1}
          step={0.01}
          value={settings.synth.envelope.attack}
          property="synthEnvelopeAttack"
          handleChange={handleValueChange}
        />
        </div>
        <div className="column">
        <SC_Slider
          name="Synth Envelope Decay"
          min={0}
          max={1}
          step={0.01}
          value={settings.synth.envelope.decay}
          property="synthEnvelopeDecay"
          handleChange={handleValueChange}
        />
        <SC_Slider
          name="Synth Envelope Release"
          min={0}
          max={1}
          step={0.01}
          value={settings.synth.envelope.release}
          property="synthEnvelopeRelease"
          handleChange={handleValueChange}
        />
        </div>
        </div>
        <div className="container_effects">
        <div className="column_effects">
        <p>DISTORTION</p>
        <SC_Slider
          name="Synth Vibrato Wet"
          min={0}
          max={1}
          step={0.01}
          value={settings.vibrato.wet}
          property="synthVibratoWet"
          handleChange={handleValueChange}
        />
        <SC_Slider
          name="Synth distortionDistortion"
          min={0}
          max={10}
          step={0.01}
          value={settings.distortion.distortion}
          property="distortionDistortion"
          handleChange={handleValueChange}
        />
        <SC_Slider
          name="Synth distortion Wet"
          min={0}
          max={1}
          step={0.01}
          value={settings.distortion.wet}
          property="distortionWet"
          handleChange={handleValueChange}
        />
        </div>
          <div className="column_effects">
            <p>PINGPONGDELAY</p>
            <div className="line"></div>
          <SC_Slider
            name="Synth AutoWah Wet"
            min={0}
            max={1}
            step={0.01}
            value={settings.autoWah.wet}
            property="AutoWahWet"
            handleChange={handleValueChange}
          />

            <SC_Slider
              name="Synth PingPongDelay Wet"
              min={0}
              max={1}
              step={0.01}
              value={settings.pingPongDelay.wet}
              property="pingPongDelayWet"
              handleChange={handleValueChange}
            />

          </div>
        </div>
        <SC_ToggleButtonSet
          name="SynthType"
          options={options}
          value={settings.synth.oscillator.type}
          property="synthType"
          handleChange={handleValueChange}
        />
      </div>
    )
  }
}
