import React, { Component } from 'react'

import * as Tone from 'tone'
import * as drumsSettings  from './tunes/drums.js'

import SC_Button from './SC_Button'

export default class Container extends Component {
  constructor(props) {
    super(props)
  }
 // Собственно синтезатор
  handleClick = () => {
    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
  }

    const phaserSettings = {
      wet: 1,
      frequency: 0.5,
      octaves: 1,
      stages: 30,
      Q: 20,
      baseFrequency: 350
    }

    const chorusSettings = {
      wet: 1,
      type: 'triangle',
      frequency: 1.5,
      delayTime: 3.5,
      depth: 3,
      spread: 180
    }

    const feedbackDelaySettings = {
      wet: 1,
      delayTime: 10,
      maxDelay: 2
    }

    const distortionSettings = {
      wet: 1,
      distortion: 4,
      oversample: '4x'
    }

    const autoWahSettings = {
      wet: 1,
      baseFrequency: 150,
      octaves: 5,
      sensitivity: 0.4,
      Q: 5,
      gain: 10,
      follower: 30
    }
    const chebyshevSettings = {
      wet: 1,
      order: 10,
      oversample: 'none'
    }

    const tremoloSettings = {
      wet: 1,
      frequency: 30,
      type: 'sine',
      depth: 0.2,
      spread: 180
    }

    const jcReverbSettings = {
      wet: 0,
      roomSize: 0.5
    }

    const crusher = new Tone.BitCrusher(4).toDestination();
    const jcReverb = new Tone.JCReverb(jcReverbSettings).toDestination();
    const tremolo = new Tone.Tremolo(tremoloSettings).toDestination().start();
    const cheby = new Tone.Chebyshev(chebyshevSettings).toDestination();
    const autoWah = new Tone.AutoWah(autoWahSettings).toDestination();
    const chorus = new Tone.Chorus(chorusSettings).toDestination().start();
    const distortion = new Tone.Distortion(distortionSettings).toDestination();
    const feedbackDelay = new Tone.FeedbackDelay(feedbackDelaySettings).toDestination();
    const phaser = new Tone.Phaser(phaserSettings).toDestination();
    const synth = new Tone.Synth(synthSettings)
    const channelSettings = { volume: 0, pan: 0, mute: false, solo: false }
    const channel = new Tone.Channel(channelSettings).toDestination()

    synth.chain(chorus,tremolo,feedbackDelay, crusher,   channel)

    const sequence = [
      {
        time: '0:0:0',
        noteName: 'A2',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'B2',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'C4',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'D2',
        duration: '1n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'G3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '1:1:0',
        noteName: 'F3',
        duration: '2n',
        velocity: 1
      },
      {
        time: '1:2:0',
        noteName: 'G3',
        duration: '2n',
        velocity: 1
      },
      {
        time: '1:3:0',
        noteName: 'A3',
        duration: '2n',
        velocity: 1
      },
      {
        time: '2:0:0',
        noteName: 'B3',
        duration: '2n',
        velocity: 1
      },
      {
        time: '2:1:0',
        noteName: 'C2',
        duration: '2n',
        velocity: 1
      },
      {
        time: '3:0:0',
        noteName: 'D2',
        duration: '1n',
        velocity: 1
      },

    ]

    // Создаём партию, добавляем в неё секвенцию
    // и включаем проигрывание
    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    // Указываем длительность партии
    part.loopEnd = '2m'

    // Включаем зацикливание
    part.loop = true

    const sampler = new Tone.Sampler({
    	urls: {
    		A1: 'melodyA1.mp3'
    	},
    	baseUrl: "http://localhost:3000/samples/",
    	// onload: () => {
    	// 	sampler.triggerAttackRelease(['A1'], 0.5);
    	// }
    }).toDestination();

    const drumsPart = new Tone.Part((time, note) => {
      sampler.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, drumsSettings.sequence.steps).start(0)

    drumsPart.loopEnd = drumsSettings.sequence.duration
    drumsPart.loop = true

    // Включаем звук в браузере
    // sampler.context.resume()

    // Включаем отсчёт времени в Tone.js
    Tone.Transport.start()



  //Запуск синта
  }


  render() {
    return (
      <div className="Container">
        <SC_Button
          text="xxx"
          handleClick={this.handleClick}
        />
      </div>
    )
  }
}
