import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let synth
let tremolo
let freeverb
let secondSynth
let distortion
let chorus
let autoWah
let autoPanner
let pingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)

    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'triangle',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const secondSynthSettings = {
      volume: 0,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'triangle',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const tremoloSettings = {
      wet: 1,
      frequency: 1000,
      type: 'sine',
      depth: 0,
      spread: 10
    }

    const autoWahSettings = {
      wet: 1,
      baseFrequency: 100,
      octaves: 2,
      sensitivity:100,
      Q: 2,
      gain: 2,
      follower: 0.1
    }

    const autoPannerSettings = {
      wet: 0,
      type: 'sine',
      frequency: 1,
      depth: 1
    }

    const distortionSettings = {
      wet: 0,
      distortion: 10,
      oversample: '4x'
    }

    const chorusSettings = {
      wet: 1,
      type: 'sine',
      frequency: 3,
      delayTime: 2,
      depth: 0.7,
      spread: 30
