import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let synth
let tremolo
let rythm
let chorus
let autoWah
let pingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)

    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'triangle',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const tremoloSettings = {
      wet: 1,
      frequency: 1000,
      type: 'sine',
      depth: 0,
      spread: 300
    }

    const autoWahSettings = {
      wet: 1,
      baseFrequency: 100,
      octaves: 2,
      sensitivity:100,
      Q: 2,
      gain: 2,
      follower: 0.1
    }

    const chorusSettings = {
      wet: 0,
      type: 'sine',
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      spread: 180
    }

    const pingPongDelaySettings = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

    this.state = {
      synthSettings,
      autoWahSettings,
      chorusSettings,
      pingPongDelaySettings
    }
  }

  handleStart = () => {
    const { synthSettings, autoWahSettings, tremoloSettings, chorusSettings, pingPongDelaySettings } = this.state

    synth = new Tone.Synth(synthSettings)
    chorus = new Tone.Chorus(chorusSettings).start()
    tremolo = new Tone.Tremolo(tremoloSettings).toDestination()
    autoWah = new Tone.AutoWah(autoWahSettings).toDestination()


    pingPongDelay = new Tone.PingPongDelay(
      pingPongDelaySettings
    ).toDestination()

    synth.chain( tremolo, pingPongDelay)

    const sequence = [
      {
      time: '0:0:0',
      noteName: 'C4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '0:1:0',
      noteName: 'E4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '0:2:0',
      noteName: 'G4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '1:0:0',
      noteName: 'D4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '1:1:0',
      noteName: 'G4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '1:2:0',
      noteName: 'B4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '2:0:0',
      noteName: 'B3',
      duration: '1n',
      velocity: 1
    },
    {
      time: '2:1:0',
      noteName: 'E3',
      duration: '1n',
      velocity: 1
    },
    {
      time: '2:2:0',
      noteName: 'G3',
      duration: '1n',
      velocity: 1
    },
    {
      time: '3:0:0',
      noteName: 'E4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '3:1:0',
      noteName: 'G4',
      duration: '1n',
      velocity: 1
    },
    {
      time: '3:2:0',
      noteName: 'B4',
      duration: '1n',
      velocity: 1
    }
    ]

    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    part.loopEnd = '2m'
    part.loop = true
    Tone.Transport.start()
  }



  handleValueChange = (property, value) => {
    const { synthSettings, tremoloSettings, autoWahSettings, chorusSettings, pingPongDelaySettings } = this.state

    if (property === 'synthType') {
      synth.oscillator.type = value
      synthSettings.oscillator.type = value
    } else if (property === 'tremoloDepth') {
      tremolo.depth.value = value
      tremoloSettings.depth = value
    } else if (property === 'tremoloSpread') {
      tremolo.spread.value = value
      tremoloSettings.spread = value
    } else if (property === 'chorusFrequency') {
      chorus.frequency.value = value
      chorusSettings.frequency = value
    }

    this.setState({
      synthSettings,
      tremoloSettings,
      chorusSettings,
      autoWahSettings,
      pingPongDelaySettings
    })
  }

  render() {
    const { synthSettings, autoWahSettings, tremoloSettings, rythmSettings, chorusSettings, pingPongDelaySettings } = this.state

    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="Container">
        <SC_Button
          text="Start"
          handleClick={this.handleStart}
        />
      <div className="ContainerOfEffects">
        <div className="FirstEffect">

            <SC_ToggleButtonSet
              name="Type"
              options={options}
              value={synthSettings.oscillator.type}
              property="synthType"
              handleChange={this.handleValueChange}
            />


            <SC_Slider
              name="Tremolo Depth"
              min={0}
              max={1}
              step={0.01}
              value={tremoloSettings.depth}
              property="tremoloDepth"
              handleChange={this.handleValueChange}
            />


            </div>
            <div>
            <SC_Button
              text="Start"
              handleClick={this.handleStartRythm}
            />
            </div>

      </div>
    </div>
    )
  }
}
